import React from 'react';
import { useState, useEffect } from 'react';
import axios from 'axios';
import { Equipment } from '../types/equipment';
import { Maker } from '../types/maker';
import { useParams } from 'react-router-dom';
function EquipmentList() {

  let {category}= useParams();
  const [equipments, setEquipments] = useState<Equipment[]>([]);
  const [cat, setCat] = useState("");
  const [makers, setMakers] = useState<Maker[]>([]);
  const url = `http://localhost:8080/api/${category}`;
  const url2 = `http://localhost:8080/categories/${category}`;
  const url3 = `http://localhost:8080/makers/`;
  useEffect(() => {
    axios.get(url).then((res) => {
      setEquipments(res.data);
  });
    axios.get(url2).then((res) => {
      setCat(res.data.title);
});
    axios.get(url3).then((res) => {
      setMakers(res.data);
    });
}, [category]);

  return (
    <>
      <h1>{cat}</h1>
      <ul>
        {
          makers && makers.map( e => <li><a href={`/${category}/${e.slug}`} className="link-danger link-offset-2 link-underline-opacity-25 link-underline-opacity-100-hover">{e.title}</a></li>)
        }
      </ul>
      <ul>
        {equipments && equipments.map(e => (
          <div className="card" style={{width: '18rem'}}>
          <img src="..." className="card-img-top" alt="..."/>
          <div className="card-body">
            <h5 className="card-title">{e.name}</h5>
            <h5 className="card-title">{e.price} VND</h5>
            <a href="#" className="btn btn-primary">Details</a>
          </div>
        </div>
        ))}
      </ul>
    </>
  );
}

export default EquipmentList;
